mapper.select:
SELECT a.SEQ,a.ID, a.CATEGORY_NAME,a.TITLE,a.PROCESS,a.REGDATE,a.QNA_CODE FROM (SELECT ROW_NUMBER() OVER(ORDER BY REGDATE DESC) RN, q.SEQ, q.ID, q.TITLE, q.CONTENT, q.REGDATE ,q.PROCESS ,c.CATEGORY_NAME,q.REF,q.QNA_CODE FROM QNA q , QNA_CATEGORY c WHERE q.QNA_CODE =c.QNA_CODE
AND q.QNA_CODE=#{qna_code}
) a WHERE CEIL(RN/5)=#{pnum} AND a.REF=a.SEQ

SELECT TITLE , REGDATE ,CONTENT
FROM NOTICE 
WHERE seq=#{seq}
