mapper.select:
 	SELECT SEQ, ID, WANTED_SEQ, REGDATE
	FROM APPLYLIST 
	WHERE id=#{id}

SELECT a.SEQ, a.ID, a.WANTED_SEQ, a.REGDATE, w.ID, NVL(l.TYPE,'N') AS listtype
FROM APPLYLIST a, WANTED_POST w, (SELECT ID, LIST_ID, "TYPE" FROM USERLIST WHERE ID = #{id}) l
WHERE  a.WANTED_SEQ = w.SEQ AND l.list_id(+) = a.ID AND NVL(l.TYPE, 'N')!='B' AND w.SEQ = #{seq}

mapper.update:
UPDATE WANTED_POST SET 
SELECTOR = #{selector} 
WHERE SEQ = #{seq}

UPDATE WANTED_POST SET 
SELECTOR = NULL 
WHERE SEQ = #{seq}

mapper.select:
SELECT SEQ, ID, TARGET_ID, "TYPE", CONTENT, SCORE, WANTED_SEQ, REGDATE, DELFLAG
FROM REVIEW_POST
WHERE ID = #{id} AND "TYPE" = #{type}

SELECT SEQ, ID, TARGET_ID, "TYPE", CONTENT, SCORE, WANTED_SEQ, REGDATE, DELFLAG
FROM REVIEW_POST
WHERE TARGET_ID = #{id} AND "TYPE" = #{type}

SELECT SEQ, ID, WANTED_SEQ 
FROM WISHLIST 
WHERE ID = #{id}

SELECT SEQ, ID, LIST_ID, "TYPE" FROM USERLIST WHERE ID = #{id}
AND "TYPE" = 'B'
AND "TYPE" = 'W'
